import React, { useEffect } from 'react'
import "./MovieDetails.css"
import { useParams } from 'react-router-dom';
import { useDispatch,useSelector } from 'react-redux'
import { GetPopularMovie } from '../Reducers/MovieReducer';
import Card from "../components/Card/Card"

function MovieDetails() {
  const {type}=useParams();
  const dispatch=useDispatch();
  const {movieType}=useSelector((state)=>state.MovieData.movieType);
  useEffect(() => {
    console.log("typewjdfssf",type);
    dispatch(GetPopularMovie({type} || 'popular'));
  }, [dispatch, type]);
  
  return (
    <div>
      <div className="movieList">
            <h2 className="listTitle">{(type ? type.toUpperCase() : "POPULAR")}</h2>
            <div className="listCards">
                {movieList &&
                movieList.filter((movie)=>{
                  if(searchData && searchData.length === 0){
                    return movie;}
                  else{
                    return movie.title.toLowerCase().includes(searchData.toLowerCase());
                  }
                })
                .map(movie => (
                      <Card key={movie.id} movie={movie} />
                    ))
                }
            </div>
        </div>
    </div>
  )
}

export default MovieDetails;
