import React from 'react'
import { useParams } from 'react-router-dom';
import { useDispatch,useSelector } from 'react-redux'
import { WatchLater } from '../Reducers/MovieReducer';
import Card from "../Card/Card"
function AddWatchLater() {
  const {type}=useParams();
  const dispatch=useDispatch();
  const movies=useSelector((state)=>state.MovieData.WatchLater);
  useEffect(() => {
    dispatch(WatchLater({type}));
  }, []);
  return (
    <div>
      <div className="movieList">
            <span className="listTitle">{(type ? type.toUpperCase() : "POPULAR")}</span>
            <div className="listCards">
                {movies &&
                movies.map(movie => (
                      <Card key={movie.id} movie={movie} />
                    ))
                }
            </div>
        </div>
    </div>
  )
}

export default AddWatchLater
