import { createSlice, createAsyncThunk } from "@reduxjs/toolkit";
const url = 'https://ott-details.p.rapidapi.com/advancedsearch?start_year=1970&end_year=2020&min_imdb=6&max_imdb=7.8&genre=action&language=english&type=movie&sort=latest&page=1';
const options = {
	method: 'GET',
	headers: {
		'X-RapidAPI-Key': '662df04e3emsh03b3a93876580d2p15f519jsn8555fd274bde',
		'X-RapidAPI-Host': 'ott-details.p.rapidapi.com'
	}
};

export const GetMovie=createAsyncThunk("GetMovie",async(data,{rejectWithValue})=>{
    const response = await fetch(url, options);
    try {
        const result = await response.json();
        console.log(result);
        return result;
} catch  (error) {
    return rejectWithValue(error);
  }
})

export const MovieDetails = createSlice({
name:"MovieDetails",
initialState:{
    movies:[],
    loding:false,
    error:null,
},
extraReducers:{
    [GetMovie.pending]:(state)=>{
        state.loding=true;
    },
    [GetMovie.fulfilled]:(state,action)=>{
        state.movie=action.payload;
        state.loding=false;
    },
    [GetMovie.rejected]:(state,action)=>{
        state.movie=action.payload;
        state.error=action.error;
    },
}
  
});
export default MovieDetails.reducer;
