import { createSlice, createAsyncThunk } from "@reduxjs/toolkit";

export const GetMovie=createAsyncThunk("GetMovie",async(data,{rejectWithValue})=>{
          const response = await fetch('https://api.themoviedb.org/3/movie/${id}?api_key=4e44d9029b1270a757cddc766a1bcb63&language=en-US'
          );
      
          try {
            const result = await response.json();
            console.log(result);
            return result;
          } catch (error) {
            return rejectWithValue(error);
          }
        }
      );

export const MovieDetails = createSlice({
name:"MovieDetails",
initialState:{
    movies:[],
    loding:false,
    error:null,
},
extraReducers:{
    [GetMovie.pending]:(state)=>{
        state.loding=true;
    },
    [GetMovie.fulfilled]:(state,action)=>{
        state.movies=action.payload;
        console.log("GetMovie.fulfilled",action.payload);
        state.loding=false;
    },
    [GetMovie.rejected]:(state,action)=>{
        state.movie=action.payload;
        state.error=action.error;
    },
}
  
});
export default MovieDetails.reducer;
