import React, { useState,useEffect } from 'react';
import { GetMovieById } from '../../Reducers/MovieReducer';
import axios from 'axios';
import { useDispatch,useSelector } from 'react-redux'
import Card from '../Card/Card';
function App() {
  const [name, setName] = useState('');
  const [movies_id,setMoives_id]=useState([])
  const [found,setfound]=useState(true)
  const dispatch=useDispatch();
  const [collection,setcollection]=useState([])
  const currentMovie=useSelector((state) => state.MovieData.movieById);

  const handleChange=(e)=>{
      setfound(true)
      setMoives_id([])
      setName(e.target.value)
  }  
  const launch=(data)=>{
    const ids= data.map(id => ({ id }));
    console.log("launch",ids);
    ids.forEach(element => {
      dispatch(GetMovieById(element))
    });
  }
  function handle(){
    console.log(collection)
  }
  const handleSubmit = async (event) => {
    event.preventDefault();
    try {
      const response = await axios.post('http://localhost:5000/recommend', {'name':name}, {
        headers: {
          'Content-Type': 'application/json', 
        },
      });
      if(response.data.not_found=='not found') {
        console.log("Movie not found:", response.data.not_found);
        setfound(false)
      } else {
        console.log('Recommended movie IDs:', response.data); 
        setMoives_id(response.data)
        launch(response.data)
      }     
    } catch (error) {
      console.error('Error sending data:', error);
      setfound(false)
    }

  };

  return (
    <div>
      <form onSubmit={handleSubmit} method="post">
        <label>
          Name:
          <input type="text" value={name} onChange={handleChange} />
        </label>
        <button type="submit">Submit</button>
      </form>
      <div className="movieList">
        <div className="listCards">
          {Array.isArray(currentMovie) && currentMovie.length > 0 ? (
            currentMovie.map((movie) => (
              <React.Fragment key={movie.id}>
                <Card movie={movie} />
                <h1>lkasjdfasdf</h1>
              </React.Fragment>
            ))
          ) : (
            <h1>No movies found</h1>
          )}
        </div>
      </div>  
        <button onClick={handle}>get</button>
        <div>{
          found?'':<h1>not present</h1>
        }</div>
    </div>
  );
}

export default App;
