import React, { useState } from 'react';
import axios from 'axios';

function Recommend() {
  const [inputData, setInputData] = useState('');
  const [predictions, setPredictions] = useState([]);

  const handlePredict = async () => {
    try {
      
      const response = await axios.post('/predict', { features: [inputData]});
      console.log("response",response.data.predictions)
      setPredictions(response.data.predictions);
    } catch (error) {
      console.error('Error predicting movies:', error);
    }
  };

  return (
    <div>
      <input
        type="text"
        placeholder="Enter data for prediction"
        value={inputData}
        onChange={(e) => setInputData(e.target.value)}
      />
      <button onClick={handlePredict}>Predict Movies</button>
      <div>
        Predictions:
        <ul>
          {predictions.map((movie, index) => (
            <li key={index}>{movie}</li>
          ))}
        </ul>
      </div>
    </div>
  );
}
export default Recommend;
