import React, { useEffect, useState } from 'react';
import { Link,useLocation } from 'react-router-dom';
import "./Header.css";
import { serachMovie } from '../Reducers/MovieReducer';
import { useDispatch } from 'react-redux';

const Header = () => {
  const [searchData,setserachData]=useState("");
  const dispatch=useDispatch();
  const location=useLocation();

  useEffect(()=>{
      dispatch(serachMovie(searchData))
  },[searchData,location.pathname]);

  useEffect(()=>{
    setserachData("")
},[location.pathname]);


  return (
    <div className='nav'>
      <div className='navleft'>
        <Link to="/">
          <img className='logo_img' alt="" src="https://upload.wikimedia.org/wikipedia/commons/thumb/6/69/IMDB_Logo_2016.svg/2560px-IMDB_Logo_2016.svg.png" />
        </Link>
        <Link to="/movie/popular" style={{textDecoration:"none"}}>
          <span>Popular</span>
        </Link>
        <Link to="/movie/top_rated" style={{textDecoration:"none"}}>
          <span>Top Rated</span>
        </Link>
        <Link to="/movie/upcoming" style={{textDecoration:"none"}}>
          <span>Upcoming</span>
        </Link>
        <Link to="/movie/upcoming" style={{textDecoration:"none"}}>
          <span>Watch</span>
        </Link>
      </div>
      <div className='navRight'>
        <input className='searchBar' value={searchData} onChange={(e)=>setserachData(e.target.value)} placeholder='search...' />
        
      </div>
    </div>
  );
}

export default Header;
