import { createSlice, createAsyncThunk } from "@reduxjs/toolkit";

export const GetMovie = createAsyncThunk("GetMovie", async (_, { rejectWithValue }) => {
    const jwtToken = 'your-jwt-token';
    const options = {
      method: 'GET',
      headers: {
        'X-RapidAPI-Key': '662df04e3emsh03b3a93876580d2p15f519jsn8555fd274bde',
        'X-RapidAPI-Host': 'ott-details.p.rapidapi.com',
        'Authorization': `Bearer ${jwtToken}`
      }
    };
  
    try {
      const response = await fetch(API_URL, options);
      const result = await response.json();
      console.log(result);
      return result;
    } catch (error) {
      return rejectWithValue(error);
    }
  });

export const MovieDetails = createSlice({
name:"MovieDetails",
initialState:{
    movies:[],
    loding:false,
    error:null,
},
extraReducers:{
    [GetMovie.pending]:(state)=>{
        state.loding=true;
    },
    [GetMovie.fulfilled]:(state,action)=>{
        state.movies=action.payload;
        console.log("GetMovie.fulfilled",action.payload);
        state.loding=false;
    },
    [GetMovie.rejected]:(state,action)=>{
        state.movie=action.payload;
        state.error=action.error;
    },
}
  
});
export default MovieDetails.reducer;
