import React, { useEffect } from 'react'
import { GetMovieById } from '../../Reducers/MovieReducer'
import { useDispatch,useSelector } from 'react-redux'
import { useParams } from 'react-router-dom';
import "./MovieDetail.css"
export default function MovieDetail() {
    const {id}=useParams();
    const dispatch = useDispatch();
    const currentMovie = useSelector((state) => state.MovieData.movieById);
    useEffect(()=>{
        dispatch(GetMovieById({id}))
    },[])
    return (
        <div className="movie">
          <div className='cover'>
            <img className="posterByid" src={`https://image.tmdb.org/t/p/original${currentMovieDetail ? currentMovieDetail.backdrop_path : ""}`} />
          </div>
            
        </div>
    )
}
