import React, { useState } from 'react';
import axios from 'axios';

function App() {
  const [name, setName] = useState('');
  const [movies_id,setMoives_id]=useState([])
  const handleSubmit = async (event) => {
    event.preventDefault();

    try {
      const response = await axios.post('http://localhost:5000/recommend', {'name':name}, {
        headers: {
          'Content-Type': 'application/json', 
        },
      });
      if (response.data.not_found=='not found') {
        console.log("Movie not found:", response.data.not_found);
      
      } else {
        console.log('Recommended movie IDs:', response.data);
        if ("not_found" in response.data) {
          console.log("Movie not found:", response.data.not_found);
        } else {
          console.log('Recommended movie IDs:', response.data);
          // Display or process the recommended movie IDs here
        }
      }
      
    } catch (error) {
      console.error('Error sending data:', error);
    }
  };

  return (
    <div>
      <form onSubmit={handleSubmit} method="post">
        <label>
          Name:
          <input type="text" value={name} onChange={(e) => setName(e.target.value)} />
        </label>
        <button type="submit">Submit</button>
      </form>
      { <div>
        {movies_id &&
          movies_id.map((value,index)=><div key={index}>{value}</div>)
        }else{
          <h1>no movie found</h1>
        }
      </div> }
    </div>
  );
}

export default App;
