import React, { useEffect } from 'react';
import { GetMovie } from '../Reducers/MovieReducer';
import { useDispatch, useSelector } from 'react-redux';
import './Home.css'
import "react-responsive-carousel/lib/styles/carousel.min.css";
import { Carousel } from 'react-responsive-carousel';
function Home() {
    const dispatch = useDispatch();
    const data = useSelector((state) => state.MovieData.movies);
    useEffect(() => {
    dispatch(GetMovie());
    }, [])
    return(
        <div>
            <div className='parent_carousel'>
                <Carousel
                showThumbs={false}
                autoPlay={true}
                transitionTime={3}
                infiniteLoop={true}
                showStatus={false}>
                    {data.map((ele)=>(
                    <div key={ele.id} >
                       <img className="poster_image" src={`https://image.tmdb.org/t/p/original${ele && ele.backdrop_path}`}></img>
                        <span className='movie_title'>{ele.title}</span>
                        <span className='movie_data'>{ele.release_date}</span>
                        <div className='movie_overview'>{ele.overview}</div>
                    </div>  
                    ))
                    }
                </Carousel>
            </div>
            <div>
             </div>
        </div>
        );
    }
  
export default Home
