import React, { useEffect } from 'react'
import "./MovieDetails.css"
import { useParams } from 'react-router-dom';
import { useDispatch,useSelector } from 'react-redux'
import { GetPopularMovie } from '../Reducers/MovieReducer';
import Card from "../components/Card/Card"
function MovieDetails() {
  const {type}=useParams();
  const dispatch=useDispatch();const movieList=useSelector((state)=>state.MovieData.GetPopularMovie);
  useEffect(()=>{
    dispatch(GetPopularMovie("Popular"));
  },[]);
  useEffect(()=>{
    dispatch(GetPopularMovie(type));
  },[type])
  
  return (
    <div>
      <div className="movie__list">
            <h2 className="list__title">{(type ? type : "POPULAR").toUpperCase()}</h2>
            <div className="list__cards">
                {
                    movieList.map((movie) => (
                        <Card movie={movie} />
                    ))
                }
            </div>
        </div>
    </div>
  )
}

export default MovieDetails
